import React, { useState } from 'react'
import CustomNavbar from '../Navbar/CustomNavbar'
import Footer from '../Footer/footer'
import imageuser from '../Image/User.png'
import './User.css';
const User = () => {
    const [UserName, SetUsername] = useState('(NOMBRE)')
    const [activeButton, SetActiveButton] = useState('');

    return (
        <div>
            <CustomNavbar></CustomNavbar>
            <div className='Userdetails'>
                <img src={imageuser} alt="User" className="user-image" />
                <h3>Welcome {UserName}!</h3>

                <button onClick={() => (SetActiveButton('Profile'))}
                    className={activeButton === "Profile" && "User-filter-button active"}> Profile</button>
                <button onClick={() => (SetActiveButton('Orders'))} className={activeButton === "Profile" && "User-filter-button active"}>Orders</button>
                <button onClick={() => (SetActiveButton('Change Password'))}>Change Password</button>
                <button onClick={() => (SetActiveButton('Delete Account'))} style={{ marginTop: "200px" }}>Delete Account</button>

            </div>
            <Footer></Footer>
        </div>
    )
}
export default User;
