import React, { useEffect, useState } from 'react'
import { getAllProducts } from './ProductService';
import './Products.css'
const GetProducts = () => {
  const [Products, setProducts] = useState([]);
  const [quantities, setQuantities] = useState(1);

  const productList = async () => {
    const products = await getAllProducts();
    setProducts(products)
  }

  useEffect(() => {
    productList()
  }, [])

  return (
    <div>
    <h1>Productos</h1>
    <div style={{ display: 'flex', flexWrap: 'wrap' }}>
      {Products.map(product => (
        <div key={product.id} className='Container-Products'>
          <h5 style={{ textAlign: 'center' }}>{product.title}</h5>
          <img src={product.image} className="card-img-top" style={{ maxWidth: '100px' }} alt={product.title} />
          <p className='Product-Price'>${product.price}</p>
          <p>{product.Description}</p>
          <div style={{ display: 'flex', justifyContent: 'center' }}>
            <div className='Container-Button-Products'>
              <button onClick={() => { quantities >= 2 && setQuantities(quantities - 1) }}>-</button>
              <input min="1" value={quantities}></input>
              <button onClick={() => (setQuantities(quantities + 1))}>+</button>
            </div>
          </div>
          <button className='Add-Product'>Add</button>
        </div>
      ))}
    </div>
  </div>
  )
}

export default GetProducts
