import React, { useEffect, useState } from 'react';
import { getAllProducts } from './ProductService';
import './Products.css';

const GetProducts = () => {
  const [Products, setProducts] = useState([]);
  const [valueInput, setValueInput] = useState(1);

  const productList = async () => {
    const products = await getAllProducts();
    setProducts(products);
  };

  useEffect(() => {
    productList();
  }, []);

  return (
    <div>
      <h1>Productos</h1>
      <div style={{ display: 'flex', flexWrap: 'wrap' }}>
        {Products.map(product => (
          <div key={product.id}>
            <div className='Container-Products'>
              <h5 style={{ textAlign: 'center' }}>{product.title}</h5>
              <img src={product.image} className="card-img-top" style={{ maxWidth: '100px' }} alt={product.title} />
              <p className='Product-Price'>${product.price}</p>
              <p>{product.Description}</p>
              <div className='Container-Button-Products'>
                <button onClick={() => setValueInput(prevValue => Math.max(prevValue - 1, 1))}>-</button>
                <input min="1" value={valueInput} onChange={(e) => setValueInput(parseInt(e.target.value) || 1)} />
                <button onClick={() => setValueInput(prevValue => prevValue + 1)}>+</button>
              </div>
              <button onClick={() => alert('Se añadieron ' + valueInput + ' unidades')} className=''>Add</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default GetProducts;
